import React, { useRef } from 'react';
import Webcam from 'react-webcam';

const EmotionDetector = () => {
  const webcamRef = useRef(null);

  return (
    <div className="p-6 flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Emotion Detector</h2>
      <Webcam
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        width={640}
        height={480}
        className="rounded shadow-md"
      />
      <p className="mt-4 text-gray-600">Webcam is live. Facial emotion detection will be added next.</p>
    </div>
  );
};

export default EmotionDetector;
